import { useEffect, useState,useCallBack} from 'react';

// import Card from '../UI/Card';
// import MealItem from './MealItem/MealItem';
import classes from './showOrders.module.css';

const ShowOrders = (props) => {
    const [orders, setOrders] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [httpError, setHttpError] = useState();
    const [btnIsHighlighted, setBtnIsHighlighted] = useState(false);
    const btnClasses = `${classes.button} ${btnIsHighlighted ? classes.bump : ''}`

   
    const fetchOrderHandler= useCallBack(async()=>{
        const response = await fetch('https://food-ordering-app-700fa-default-rtdb.firebaseio.com/orders.json');
  
       if (!response.ok) {
          throw new Error('Something went wrong!');
        }
  
        const responseData = await response.json();
  
        const loadedOrders = [];
  
        for (const key in responseData) {
          loadedOrders.push({
            id: key,
            name: responseData[key].user.name,
            city: responseData[key].user.city,
            pin: responseData[key].user.pin,

            pname:responseData[key].orderedItems[0].name,
            price:responseData[key].orderedItems[0].price,
          });
        }
  console.log(loadedOrders);
        setOrders(loadedOrders);
        setIsLoading(false);

        fetchOrderHandler().catch((error) => {
        setIsLoading(false);
        setHttpError(error.message);
      });
    }
  ,[]);
  useEffect(()=>{
        
    fetchOrderHandler();

},[]);
      
    
  
    if (isLoading) {
      return (
        <section className={classes.MealsLoading}>
          <p>Loading...</p>
        </section>
      );
    }
  
    if (httpError) {
      return (
        <section className={classes.MealsError}>
          <p>{httpError}</p>
        </section>
      );
    }
  
    
    
    //   const orderList = orders.map((orders) => (
    //     <MealItem
    //       key={meal.id}
    //       id={meal.id}
    //       name={meal.name}
    //       description={meal.description}
    //       price={meal.price}
    //     />
    //   ));
    




  return (
    <div>
    <li className={classes['btn-list-order']}>
<button className={btnClasses} onClick={fetchOrderHandler}>  
<span>Show orders</span>
</button>
</li> 
</div>
  );
};

export default ShowOrders;
